declare type MyPick<T, K extends keyof T> = {[P in K]: T[P]};
declare type Obj = {a: number, b: string, c: boolean};
declare const obj1: MyPick<Obj, "a" | "c">;
declare const a1: number;
declare const b1: never;
declare const c1: boolean;
declare type MyPartial<T> = {[P in keyof T]?: T[P]};
declare type PartialObj = MyPartial<Obj>;
declare const obj2: PartialObj;
declare const a2: number | undefined;
declare const b2: string | undefined;
declare const c2: boolean | undefined;
declare type MyRequired<T> = {[P in keyof T]-?: T[P]};
declare type RequiredObj = MyRequired<PartialObj>;
declare const obj3: RequiredObj;
declare const a3: number;
declare const b3: string;
declare const c3: boolean;
declare type MyRecord<K extends keyof any, T> = {[P in K]: T};
declare const record: MyRecord<"email" | "username", string>;
declare const email: string;
declare const username: string;
declare type MyExclude<T, U> = T extends U ? never : T;
declare type MyExtract<T, U> = T extends U ? T : never;
declare type MyOmit<T, K extends keyof any> = MyPick<T, MyExclude<keyof T, K>>;
declare const obj4: MyOmit<Obj, "b">;
declare const a4: number;
declare const b4: never;
declare const c4: boolean;
